import React, { useEffect, useState } from 'react';
import { Layout, Carousel, Row, Col, Card, Typography, Button } from 'antd';
import axios from 'axios';

const { Header, Content, Footer } = Layout;
const { Title, Paragraph } = Typography;
const { Meta } = Card;

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); // Thêm trạng thái loading

    // Hàm fetch dữ liệu sản phẩm từ API
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8888/api/v1/product'); // URL của API lấy sản phẩm
            setProducts(response.data); // Lưu dữ liệu sản phẩm vào state
        } catch (error) {
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false); // Đặt trạng thái loading thành false khi đã nhận dữ liệu
        }
    };

    useEffect(() => {
        fetchProducts(); // Gọi hàm fetchProducts khi component được render lần đầu
    }, []);

    return (
        <Layout>
            {/* Carousel */}
            <Carousel autoplay>
                <div>
                    <div style={{ backgroundColor: '#254753', height: '300px', color: 'white', textAlign: 'center' }}>
                        <Title level={2}>Welcome to Mechanical Keyboard Shop</Title>
                        <Paragraph>Explore the latest and premium mechanical keyboards</Paragraph>
                    </div>
                </div>
                <div>
                    <div style={{ backgroundColor: '#254753', height: '300px', color: 'white', textAlign: 'center' }}>
                        <Title level={2}>Best Deals on Top Brands</Title>
                        <Paragraph>Grab exclusive discounts on your favorite keyboards</Paragraph>
                    </div>
                </div>
            </Carousel>

            {/* Content */}
            <Content style={{ padding: '50px' }}>
                <Title level={3} style={{ textAlign: 'center' }}>
                    Featured Products
                </Title>
                <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                    {loading ? (
                        <p>Loading products...</p>
                    ) : (
                        products.map((product) => (
                            <Col key={product.id} xs={24} sm={12} md={8} lg={6}>
                                <Card
                                    hoverable
                                    cover={<img alt={product.name} src={`http://localhost:8888/${product.image}`} />}
                                    style={{ borderRadius: '8px' }}
                                >
                                    <Meta title={product.product_name} description={`Price: ${product.price}`} />
                                    <Button
                                        type="primary"
                                        style={{ marginTop: '10px', width: '100%' }}
                                        onClick={() => alert(`You selected ${product.name}`)}
                                    >
                                        Add to Cart
                                    </Button>
                                </Card>

                            </Col>
                        ))
                    )}
                </Row>
            </Content>
        </Layout>
    );
};

export default HomePage;
