import React from 'react';
import { Layout, Menu, Button } from 'antd';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from './redux/actions'; // Assume you have a logout action

const { Header } = Layout;

const AppHeader = () => {
  const { isAuthenticated, user } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logout()); // Dispatch the logout action
    console.log('Logging out');
  };

  return (
    <Header style={{ backgroundColor: '#254753' }}>
      <div className="logo" style={{ color: 'white', fontSize: '1.5rem', fontWeight: 'bold' }}>
        Mechanical Keyboard Shop
      </div>
      <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ justifyContent: 'end' }}>
        <Menu.Item key="1"><Link to="/">Home</Link></Menu.Item>
        <Menu.Item key="2"><Link to="/shop">Shop</Link></Menu.Item>
        <Menu.Item key="3"><Link to="/contact">Contact</Link></Menu.Item>
        {
          isAuthenticated ? (
            <Menu.Item key="4" style={{ display: 'flex', alignItems: 'center' }}>
              <span style={{ marginRight: 10, color: 'white' }}>{user.username}</span>
              <Button type="primary" onClick={handleLogout}>Logout</Button>
            </Menu.Item>
          ) : (
            <Menu.Item key="4"><Link to="/login">Login</Link></Menu.Item>
          )
        }
      </Menu>
    </Header>
  );
};

export default AppHeader;
