import React, { useEffect, useState } from 'react';
import { Layout, Row, Col, Card, Typography, Button } from 'antd';
import axios from 'axios';

const { Content } = Layout;
const { Title } = Typography;
const { Meta } = Card;

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state

    // Fetch products data from API
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8888/api/v1/product');
            setProducts(response.data); // Save products data into state
        } catch (error) {
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false); // Set loading to false once data is received
        }
    };

    useEffect(() => {
        fetchProducts(); // Call fetchProducts when component is first rendered
    }, []);

    return (
        <Layout>
            {/* Content */}
            <Content style={{ padding: '100px 200px' }}>
                <Title level={3} style={{ textAlign: 'center' }}>
                    Featured Products
                </Title>
                <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                    {loading ? (
                        <p>Loading products...</p>
                    ) : (
                        products.map((product) => (
                            <Col key={product.id} xs={24} sm={12} md={8} lg={6}>
                                <Card
                                    hoverable
                                    cover={
                                        <div style={{ 
                                            display: 'flex', 
                                            justifyContent: 'center', 
                                            alignItems: 'center', 
                                            height: '240px' 
                                        }}>
                                            <img
                                                alt={product.name}
                                                src={`http://localhost:8888/${product.image}`}
                                                style={{
                                                    maxWidth: '100%',  // Ensure image stays within bounds
                                                    maxHeight: '100%', // Ensure image stays within bounds
                                                    objectFit: 'cover', // Maintain aspect ratio and cover the area
                                                }}
                                            />
                                        </div>
                                    }
                                    style={{ borderRadius: '8px', border: 'none' }}
                                >
                                    <div className="product-info" style={{ padding: '10px' }}>
                                        <Title level={4}>{product.product_name}</Title>
                                        <div className="price-box" style={{ marginBottom: '10px' }}>
                                            <span>{product.price}₫</span>
                                        </div>
                                        <div className="product-action" style={{ display: 'flex', justifyContent: 'space-between' }}>
                                            <Button
                                                type="link"
                                                style={{ padding: 0 }}
                                                onClick={() => alert(`Added ${product.product_name} to wishlist`)}
                                            >
                                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16">
                                                    <path d="M295.71 35.522c-34.43-0.184-67.161 14.937-89.339 41.273-22.039-26.516-54.861-41.68-89.339-41.273-64.633 0-117.03 52.395-117.03 117.03 0 110.76 193.31 218.91 201.14 223.09 3.162 2.113 7.286 2.113 10.449 0 7.837-4.18 201.14-110.76 201.14-223.09 0-64.633-52.396-117.03-117.03-117.03zm-89.339 319.22c-30.302-17.763-185.47-112.33-185.47-202.19 0-53.091 43.039-96.131 96.131-96.131 32.512-0.427 62.938 15.972 80.457 43.363 3.557 4.905 10.418 5.998 15.323 2.44 0.937-0.68 1.761-1.503 2.44-2.44 29.055-44.435 88.631-56.903 133.07-27.848 27.202 17.787 43.575 48.114 43.521 80.615 1e-3 90.907-155.17 184.95-185.47 202.19z"></path>
                                                </svg>
                                                Add to Wishlist
                                            </Button>
                                            <Button
                                                type="primary"
                                                onClick={() => alert(`Added ${product.product_name} to cart`)}
                                            >
                                                Add to Cart
                                            </Button>
                                        </div>
                                    </div>
                                </Card>
                            </Col>
                        ))
                    )}
                </Row>
            </Content>
        </Layout>
    );
};

export default HomePage;
