import React from 'react';
import { Layout, Menu, Button, Typography } from 'antd';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../redux/actions';

const { Header } = Layout;
const { Text } = Typography;

const AppHeader = () => {
  const { isAuthenticated, user } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logout()); // Dispatch the logout action
    console.log('Logging out');
  };

  return (
    <Header style={{  padding: '0 20px' }}>
      <div className="logo" style={{ color: 'white', fontSize: '1.8rem', fontWeight: 'bold', float: 'left' }}>
        Mechanical Keyboard Shop
      </div>
      <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ justifyContent: 'flex-end', lineHeight: '64px' }}>
        <Menu.Item key="1">
          <Link to="/">Home</Link>
        </Menu.Item>
        <Menu.Item key="2">
          <Link to="/shop">Shop</Link>
        </Menu.Item>
        <Menu.Item key="3">
          <Link to="/contact">Contact</Link>
        </Menu.Item>
        {
          isAuthenticated ? (
            <Menu.Item key="4" style={{ display: 'flex', alignItems: 'center' }}>
              <Text style={{ color: 'white', marginRight: 10 }}>{user.username}</Text>
              <Button type="default" onClick={handleLogout} style={{ marginLeft: 10, backgroundColor: '#1890ff', borderColor: '#1890ff', color: 'white' }}>
                Logout
              </Button>
            </Menu.Item>
          ) : (
            <Menu.Item key="4">
              <Link to="/login">Login</Link>
            </Menu.Item>
          )
        }
      </Menu>
    </Header>
  );
};

export default AppHeader;
