import React, { useEffect, useState } from 'react';
import { Layout, Row, Col, Card, Typography, Button } from 'antd';
import axios from 'axios';

const { Content } = Layout;
const { Title } = Typography;
const { Meta } = Card;

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state

    // Fetch products data from API
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8888/api/v1/product');
            setProducts(response.data); // Save products data into state
        } catch (error) {
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false); // Set loading to false once data is received
        }
    };

    useEffect(() => {
        fetchProducts(); // Call fetchProducts when component is first rendered
    }, []);

    return (
        <Layout>
            {/* Content */}
            <Content style={{ padding: '100px 200px' }}>
                <Title level={3} style={{ textAlign: 'center' }}>
                    Featured Products
                </Title>
                <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                    {loading ? (
                        <p>Loading products...</p>
                    ) : (
                        products.map((product) => (
                            <Col key={product.id} xs={24} sm={12} md={8} lg={6}>
                                <Card
                                    hoverable
                                    cover={
                                        <div style={{ 
                                            display: 'flex', 
                                            justifyContent: 'center', 
                                            alignItems: 'center', 
                                            height: '240px' 
                                        }}>
                                            <img
                                                alt={product.name}
                                                src={`http://localhost:8888/${product.image}`}
                                                style={{
                                                    maxWidth: '100%',  // Ensure image stays within bounds
                                                    maxHeight: '240px', // Ensure image stays within bounds
                                                    objectFit: 'cover', // Maintain aspect ratio and cover the area
                                                }}
                                            />
                                        </div>
                                    }
                                    style={{ borderRadius: '8px', border: 'none' }}
                                >
                                    <div className="product-info" style={{ padding: '10px' }}>
                                        <Title level={4}>{product.product_name}</Title>
                                        <div className="price-box" style={{ marginBottom: '10px' }}>
                                            <span>{product.price}₫</span>
                                        </div>
                                        <div className="product-action" style={{ display: 'flex', justifyContent: 'space-between' }}>
                                            <Button
                                                type="link"
                                                style={{ padding: 0 }}
                                                onClick={() => alert(`Added ${product.product_name} to wishlist`)}
                                            >
                                                Add to Wishlist
                                            </Button>
                                            <Button
                                                type="primary"
                                                onClick={() => alert(`Added ${product.product_name} to cart`)}
                                            >
                                                Add to Cart
                                            </Button>
                                        </div>
                                    </div>
                                </Card>
                            </Col>
                        ))
                    )}
                </Row>
            </Content>
        </Layout>
    );
};

export default HomePage;
