import React, { useEffect, useState } from 'react';
import { Layout, Row, Col, Card, Typography, Button } from 'antd';
import axios from 'axios';

const { Content } = Layout;
const { Title } = Typography;
const { Meta } = Card;

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch products data from API
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8888/api/v1/product');
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchProducts(); // Fetch products when the component is first rendered
    }, []);

    return (
        <Layout>
            <Content style={{ padding: '100px 200px' }}>
                <Title level={3} style={{ textAlign: 'center' }}>
                    Featured Products
                </Title>
                <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                    {loading ? (
                        <p>Loading products...</p>
                    ) : (
                        products.map((product) => (
                            <Col key={product.id} xs={24} sm={12} md={8} lg={6}>
                                <div className="item_product_main">
                                    <form
                                        action="/cart/add"
                                        method="post"
                                        className="variants product-action wishItem"
                                        data-cart-form=""
                                        data-id={`product-actions-${product.id}`}
                                        enctype="multipart/form-data"
                                    >
                                        <div className="product-thumbnail">
                                            <a
                                                className="image_thumb"
                                                href={`/product/${product.id}`}
                                                title={product.name}
                                                style={{ height: '226px' }}
                                            >
                                                <picture>
                                                    <source
                                                        media="(min-width: 1200px)"
                                                        srcSet={`http://localhost:8888/${product.image}`}
                                                    />
                                                    <source
                                                        media="(min-width: 992px)"
                                                        srcSet={`http://localhost:8888/${product.image}`}
                                                    />
                                                    <source
                                                        media="(min-width: 569px)"
                                                        srcSet={`http://localhost:8888/${product.image}`}
                                                    />
                                                    <source
                                                        media="(max-width: 480px)"
                                                        srcSet={`http://localhost:8888/${product.image}`}
                                                    />
                                                    <img
                                                        width="240"
                                                        height="240"
                                                        src={`http://localhost:8888/${product.image}`}
                                                        alt={product.name}
                                                        className="lazyload img-responsive center-block"
                                                    />
                                                </picture>
                                            </a>
                                        </div>
                                        <div className="product-info">
                                            <h3 className="product-name">
                                                <a href={`/product/${product.id}`} title={product.name}>
                                                    {product.name}
                                                </a>
                                            </h3>
                                            <div className="price-box">
                                                {product.price}₫
                                                <div className="action-cart">
                                                    <input
                                                        type="hidden"
                                                        name="variantId"
                                                        value={product.id}
                                                    />
                                                    <button
                                                        className="btn-buy btn-views add_to_cart"
                                                        title="Mua ngay"
                                                        onClick={() => alert(`You selected ${product.name}`)}
                                                    >
                                                        <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25">
                                                            <circle cx="7" cy="17" r="2"></circle>
                                                            <circle cx="15" cy="17" r="2"></circle>
                                                            <path d="M20,4.4V5l-1.8,6.3c-0.1,0.4-0.5,0.7-1,0.7H6.7c-0.4,0-0.8-0.3-1-0.7L3.3,3.9C3.1,3.3,2.6,3,2.1,3H0.4C0.2,3,0,2.8,0,2.6 V1.4C0,1.2,0.2,1,0.4,1h2.5c1,0,1.8,0.6,2.1,1.6L5.1,3l2.3,6.8c0,0.1,0.2,0.2,0.3,0.2h8.6c0.1,0,0.3-0.1,0.3-0.2l1.3-4.4 C17.9,5.2,17.7,5,17.5,5H9.4C9.2,5,9,4.8,9,4.6V3.4C9,3.2,9.2,3,9.4,3h9.2C19.4,3,20,3.6,20,4.4z"></path>
                                                        </svg>
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                            </Col>
                        ))
                    )}
                </Row>
            </Content>
        </Layout>
    );
};

export default HomePage;
