import React from 'react';
import { Layout, Menu, Carousel, Row, Col, Card, Typography, Button } from 'antd';

const { Header, Content, Footer } = Layout;
const { Title, Paragraph } = Typography;
const { Meta } = Card;

const carouselItems = [
  {
    title: 'Welcome to Mechanical Keyboard Shop',
    description: 'Explore the latest and premium mechanical keyboards',
    image: 'https://example.com/banner1.jpg', // Thay bằng URL ảnh của bạn
  },
  {
    title: 'Best Deals on Top Brands',
    description: 'Grab exclusive discounts on your favorite keyboards',
    image: 'https://example.com/banner2.jpg', // Thay bằng URL ảnh của bạn
  },
];

const productData = [
  {
    id: 1,
    name: 'Keychron K2',
    price: '80.00 USD',
    image: 'https://example.com/product1.jpg', // Thay bằng URL ảnh của bạn
  },
  {
    id: 2,
    name: 'Logitech MX Mechanical',
    price: '120.00 USD',
    image: 'https://example.com/product2.jpg',
  },
  {
    id: 3,
    name: 'Razer BlackWidow V3',
    price: '140.00 USD',
    image: 'https://example.com/product3.jpg',
  },
];

const HomePage = () => {
  return (
    <Layout>
      {/* Header */}
      <Header style={{ backgroundColor: '#254753' }}>
        <div className="logo" style={{ color: 'white', fontSize: '1.5rem', fontWeight: 'bold' }}>
          Mechanical Keyboard Shop
        </div>
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ justifyContent: 'end' }}>
          <Menu.Item key="1">Home</Menu.Item>
          <Menu.Item key="2">Shop</Menu.Item>
          <Menu.Item key="3">Contact</Menu.Item>
          <Menu.Item key="4">Login</Menu.Item>
        </Menu>
      </Header>

      {/* Carousel */}
      <Carousel autoplay>
        {carouselItems.map((item, index) => (
          <div key={index}>
            <div
              style={{
                backgroundImage: `url(${item.image})`,
                backgroundSize: 'cover',
                height: '300px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                color: 'white',
                textAlign: 'center',
              }}
            >
              <Title level={2} style={{ color: 'white' }}>{item.title}</Title>
              <Paragraph>{item.description}</Paragraph>
            </div>
          </div>
        ))}
      </Carousel>

      {/* Content */}
      <Content style={{ padding: '50px' }}>
        <Title level={3} style={{ textAlign: 'center' }}>
          Featured Products
        </Title>
        <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
          {productData.map((product) => (
            <Col key={product.id} xs={24} sm={12} md={8} lg={6}>
              <Card
                hoverable
                cover={<img alt={product.name} src={product.image} />}
                style={{ borderRadius: '8px' }}
              >
                <Meta title={product.name} description={`Price: ${product.price}`} />
                <Button
                  type="primary"
                  style={{ marginTop: '10px', width: '100%' }}
                  onClick={() => alert(`You selected ${product.name}`)}
                >
                  Add to Cart
                </Button>
              </Card>
            </Col>
          ))}
        </Row>
      </Content>

      {/* Footer */}
      <Footer style={{ textAlign: 'center', backgroundColor: '#254753', color: 'white' }}>
        © 2024 Mechanical Keyboard Shop. All Rights Reserved.
      </Footer>
    </Layout>
  );
};

export default HomePage;
